import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
function Order() {
  const [orders, setOrders] = useState([]);
  const [newOrder, setNewOrder] = useState({
    // order_id: '',
    // order_date: '',
    // order_total_price: '',
    // customer_id: '',
    invoice_id: '',
    vendor_id: '',
    dept_id: '',
    TotalAmt: '',
    VendorPayment: '',
  });
  const [editOrder, setEditOrder] = useState({
    // order_id: '',
    // order_date: '',
    // order_total_price: '',
    // customer_id: '',
    invoice_id: '',
    vendor_id: '',
    dept_id: '',
    TotalAmt: '',
    VendorPayment: '',
  });
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  useEffect(() => {
    // Fetch data from the Node.js server
    fetchOrders();
  }, []);

  const fetchOrders = () => {
    axios.get('http://localhost:8089/api/invoices')
      .then(response => {
        console.log('Fetched Invoices:', response.data);
        setOrders(response.data || []);
      })
      .catch(error => console.error(error));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewOrder(prevOrder => ({
      ...prevOrder,
      [name]: value,
    }));
  };

  const handleAddOrder = () => {
    axios.post('http://localhost:8089/api/invoices', newOrder)
      .then(response => {
        console.log('Added Invoice:', response.data);
        fetchOrders();
        setNewOrder({
          // order_id: '',
          // order_date: '',
          // order_total_price: '',
          // customer_id: '',
          invoice_id: '',
    vendor_id: '',
    dept_id: '',
    TotalAmt: '',
    VendorPayment: '',
        });
      })
      .catch(error => console.error(error));
  };

  const handleDeleteOrder = (invoiceId) => {
    axios.delete(`http://localhost:8089/api/invoices/${invoiceId}`)
      .then(response => {
        console.log('Deleted Order:', response.data);
        fetchOrders();
      })
      .catch(error => console.error(error));
  };

  const handleEditOrder = (order) => {
    setEditOrder(order);
    setIsEditModalOpen(true);
  };

  const handleUpdateOrder = () => {
    axios.put(`http://localhost:8089/api/invoices/${editOrder.invoice_id}`, editOrder)
      .then(response => {
        console.log('Updated Invoice:', response.data);
        fetchOrders();
        setIsEditModalOpen(false);
      })
      .catch(error => console.error(error));
  };

  return (
    <div className="container">
      <h1>Invoice Records</h1>
      <div className="navbar">
        <a href="/">Home</a>
        <a href="/departments">Departments</a>
        <a href="/deptEmployee">Department Employee</a>
        <a href="/employee">Employee</a>
        <a href="/invoice">Invoice</a>
        <a href="/product">Product</a>
        <a href="/productionEmp">Production Employee</a>
        <a href="/productionLine">Production Line</a>
        <a href="/productLine">Product Line</a>
        <a href="/productMaterial">Product Material</a>
        <a href="/rawMaterial">Raw_Material</a>
        <a href="/supplySchedule">SupplySchedule</a>
        <a href="/vendor">Vendor</a>
        <a href="/warehouse">WareHouse</a>
        <a href="/warehouseEmp">Warehouse Employee</a>
        {/* Add other links as needed */}
      </div>
      <table>
        <thead>
          <tr>
            <th>Invoice ID</th>
            <th>Vendor ID</th>
            <th>Department ID</th>
            <th>Total Amount</th>
            <th>VendorPayment</th>
          </tr>
        </thead>
        <tbody>
          {orders && Array.isArray(orders) ? (
            orders.map(order => (
              <tr key={order.invoice_id}>
                <td>{order.invoice_id}</td>
                <td>{order.vendor_id}</td>
                <td>{order.dept_id}</td>
                <td>{order.TotalAmt}</td>
                <td>{order.VendorPayment}</td>
                <td>
                  <button onClick={() => handleEditOrder(order)}>
                    Edit
                  </button>
                  <button onClick={() => handleDeleteOrder(order.invoice_id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">Loading...</td>
            </tr>
          )}
        </tbody>
      </table>

      <h2>Add New Invoice</h2>
      <div>
        <label>Invoice ID:</label>
        <input
          type="text"
          name="invoice_id"
          value={newOrder.invoice_id}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Vendor ID:</label>
        <input
          type="text"
          name="vendor_id"
          value={newOrder.vendor_id}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Department ID:</label>
        <input
          type="text"
          name="dept_id"
          value={newOrder.dept_id}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Total Amount:</label>
        <input
          type="text"
          name="TotalAmt"
          value={newOrder.TotalAmt}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Vendor Payment:</label>
        <input
          type="text"
          name="VendorPayment"
          value={newOrder.VendorPayment}
          onChange={handleInputChange}
        />
      </div>
      <button onClick={handleAddOrder}>Add Invoice</button>

      {/* Edit Modal */}
      {isEditModalOpen && (
        <div>
          <h2>Edit Invoice</h2>
          <div>
            <label>ID:</label>
            <input
              type="text"
              name="invoice_id"
              value={editOrder.invoice_id}
              disabled
            />
          </div>
          <div>
            <label>Vendor ID:</label>
            <input
              type="text"
              name="vendor_id"
              value={editOrder.vendor_id}
              onChange={(e) => setEditOrder({ ...editOrder, vendor_id: e.target.value })}
            />
          </div>
          <div>
            <label>Department ID:</label>
            <input
              type="text"
              name="dept_id"
              value={editOrder.dept_id}
              onChange={(e) => setEditOrder({ ...editOrder, dept_id: e.target.value })}
            />
          </div>
          <div>
            <label>Total Amount:</label>
            <input
              type="text"
              name="TotalAmt"
              value={editOrder.TotalAmt}
              onChange={(e) => setEditOrder({ ...editOrder, TotalAmt: e.target.value })}
            />
          </div>
          <div>
            <label>Vendor Payment:</label>
            <input
              type="text"
              name="VendorPayment"
              value={editOrder.VendorPayment}
              onChange={(e) => setEditOrder({ ...editOrder, VendorPayment: e.target.value })}
            />
          </div>
          <button onClick={handleUpdateOrder}>Update Invoice</button>
          <button onClick={() => setIsEditModalOpen(false)}>Cancel</button>
        </div>
      )}
      <br></br>
    <br></br>
    </div>
  );
}

export default Order;
