import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
function OrderItem() {
  const [orderItems, setOrderItems] = useState([]);
  const [newOrderItem, setNewOrderItem] = useState({
    // order_item_id: '',
    // order_item_quantity: '',
    // order_item_totalprice: '',
    // order_id: '',
    // stock_id: '',
    product_id: '',
    emp_id: '',
    dept_id: '',
    pName: '',
    color: '',
    weight: '',
    cost: '',
    price: '',

  });
  const [editOrderItem, setEditOrderItem] = useState({
    // order_item_id: '',
    // order_item_quantity: '',
    // order_item_totalprice: '',
    // order_id: '',
    // stock_id: '',
    product_id: '',
    emp_id: '',
    dept_id: '',
    pName: '',
    color: '',
    weight: '',
    cost: '',
    price: '',
  });
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  useEffect(() => {
    // Fetch data from the Node.js server
    fetchOrderItems();
  }, []);

  const fetchOrderItems = () => {
    axios.get('http://localhost:8089/api/products')
      .then(response => {
        console.log('Fetched Products:', response.data);
        setOrderItems(response.data || []);
      })
      .catch(error => console.error(error));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewOrderItem(prevOrderItem => ({
      ...prevOrderItem,
      [name]: value,
    }));
  };

  const handleAddOrderItem = () => {
    axios.post('http://localhost:8089/api/products', newOrderItem)
      .then(response => {
        console.log('Added Product:', response.data);
        fetchOrderItems();
        setNewOrderItem({
          // order_item_id: '',
          // order_item_quantity: '',
          // order_item_totalprice: '',
          // order_id: '',
          // stock_id: '',
          product_id: '',
    emp_id: '',
    dept_id: '',
    pName: '',
    color: '',
    weight: '',
    cost: '',
    price: '',
        });
      })
      .catch(error => console.error(error));
  };

  const handleDeleteOrderItem = (productID) => {
    axios.delete(`http://localhost:8089/api/products/${productID}`)
      .then(response => {
        console.log('Deleted Product:', response.data);
        fetchOrderItems();
      })
      .catch(error => console.error(error));
  };

  const handleEditOrderItem = (orderItem) => {
    setEditOrderItem(orderItem);
    setIsEditModalOpen(true);
  };

  const handleUpdateOrderItem = () => {
    axios.put(`http://localhost:8089/api/products/${editOrderItem.product_id}`, editOrderItem)
      .then(response => {
        console.log('Updated Product:', response.data);
        fetchOrderItems();
        setIsEditModalOpen(false);
      })
      .catch(error => console.error(error));
  };

  return (
    <div className="container">
      <h1>Order Item Records</h1>
      <div className="navbar">
        <a href="/">Home</a>
        <a href="/departments">Departments</a>
        <a href="/deptEmployee">Department Employee</a>
        <a href="/employee">Employee</a>
        <a href="/invoice">Invoice</a>
        <a href="/product">Product</a>
        <a href="/productionEmp">Production Employee</a>
        <a href="/productionLine">Production Line</a>
        <a href="/productLine">Product Line</a>
        <a href="/productMaterial">Product Material</a>
        <a href="/rawMaterial">Raw_Material</a>
        <a href="/supplySchedule">SupplySchedule</a>
        <a href="/vendor">Vendor</a>
        <a href="/warehouse">WareHouse</a>
        <a href="/warehouseEmp">Warehouse Employee</a>
        {/* Add other links as needed */}
      </div>
      <table>
        <thead>
          <tr>
            <th>Product ID</th>
            <th>Employee ID</th>
            <th>Department ID</th>
            <th>Quantity</th>
            <th>Total Price</th>
            <th>Order ID</th>
            <th>Stock ID</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orderItems && Array.isArray(orderItems) ? (
            orderItems.map(orderItem => (
              <tr key={orderItem.product_id}>
                <td>{orderItem.product_id}</td>
                <td>{orderItem.emp_id}</td>
                <td>{orderItem.dept_id}</td>
                <td>{orderItem.pName}</td>
                <td>{orderItem.color}</td>
                <td>{orderItem.weight}</td>
                <td>{orderItem.cost}</td>
                <td>{orderItem.price}</td>
                <td>
                  <button onClick={() => handleEditOrderItem(orderItem)}>
                    Edit
                  </button>
                  <button onClick={() => handleDeleteOrderItem(orderItem.product_id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">Loading...</td>
            </tr>
          )}
        </tbody>
      </table>

      <h2>Add New Order Item</h2>
      <div>
        <label>ID:</label>
        <input
          type="text"
          name="order_item_id"
          value={newOrderItem.order_item_id}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Quantity:</label>
        <input
          type="text"
          name="order_item_quantity"
          value={newOrderItem.order_item_quantity}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Total Price:</label>
        <input
          type="text"
          name="order_item_totalprice"
          value={newOrderItem.order_item_totalprice}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Order ID:</label>
        <input
          type="text"
          name="order_id"
          value={newOrderItem.order_id}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Stock ID:</label>
        <input
          type="text"
          name="stock_id"
          value={newOrderItem.stock_id}
          onChange={handleInputChange}
        />
      </div>
      <button onClick={handleAddOrderItem}>Add Order Item</button>

      {/* Edit Modal */}
      {isEditModalOpen && (
        <div>
          <h2>Edit Order Item</h2>
          <div>
            <label>ID:</label>
            <input
              type="text"
              name="order_item_id"
              value={editOrderItem.order_item_id}
              disabled
            />
          </div>
          <div>
            <label>Quantity:</label>
            <input
              type="text"
              name="order_item_quantity"
              value={editOrderItem.order_item_quantity}
              onChange={(e) => setEditOrderItem({ ...editOrderItem, order_item_quantity: e.target.value })}
            />
          </div>
          <div>
            <label>Total Price:</label>
            <input
              type="text"
              name="order_item_totalprice"
              value={editOrderItem.order_item_totalprice}
              onChange={(e) => setEditOrderItem({ ...editOrderItem, order_item_totalprice: e.target.value })}
            />
          </div>
          <div>
            <label>Order ID:</label>
            <input
              type="text"
              name="order_id"
              value={editOrderItem.order_id}
              onChange={(e) => setEditOrderItem({ ...editOrderItem, order_id: e.target.value })}
            />
          </div>
          <div>
            <label>Stock ID:</label>
            <input
              type="text"
              name="stock_id"
              value={editOrderItem.stock_id}
              onChange={(e) => setEditOrderItem({ ...editOrderItem, stock_id: e.target.value })}
            />
          </div>
          <button onClick={handleUpdateOrderItem}>Update Order Item</button>
          <button onClick={() => setIsEditModalOpen(false)}>Cancel</button>
        </div>
      )}
      <br></br>
    <br></br>
    <Link to="/" className="nav-link">Home</Link>
    </div>
  );
}

export default OrderItem;
