import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Customer() {
  const [deptEmp, setDeptEmp] = useState([]);
  const [newDeptEmp, setNewDeptEmp] = useState({
    // customer_id: '',
    // customer_name: '',
    // customer_phone: '',
    // customer_email: '',
    emp_id: '',
    dept_id: '',
  });
  const [editDeptEmp, setEditDeptEmp] = useState({
    // customer_id: '',
    // customer_name: '',
    // customer_phone: '',
    // customer_email: '',
    emp_id: '',
    dept_id: '',
  });
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  useEffect(() => {
    // Fetch data from the Node.js server
    fetchDeptEmps();
  }, []);

  const fetchDeptEmps = () => {
    axios.get('http://localhost:8089/api/deptEmps')
      .then(response => {
        console.log('Fetched Customers:', response.data);
        // Access the nested array within the data property
        setDeptEmp(response.data || []);
      })
      .catch(error => console.error(error));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewDeptEmp(prevCustomer => ({
      ...prevCustomer,
      [name]: value,
    }));
  };

  const handleAddDeptEmp = () => {
    axios.post('http://localhost:8089/api/deptEmps', newDeptEmp)
      .then(response => {
        console.log('Added Department Employee:', response.data);
        // Fetch updated list of customers
        fetchDeptEmps();
        // Clear the input fields
        setNewDeptEmp({
          // customer_id: '',
          // customer_name: '',
          // customer_phone: '',
          // customer_email: '',
          emp_id: '',
    dept_id: '',
        });
      })
      .catch(error => console.error(error));
  };

  const handleDeleteDeptEmp = (emp_id) => {
    axios.delete(`http://localhost:8089/api/deptEmps/${emp_id}`)
      .then(response => {
        console.log('Deleted Department Employee:', response.data);
        // Fetch updated list of customers
        fetchDeptEmps();
      })
      .catch(error => console.error(error));
  };

  const handleEditDeptEmp = (customer) => {
    setEditDeptEmp(customer);
    setIsEditModalOpen(true);
  };

  const handleUpdateDeptEmp = () => {
    axios.put(`http://localhost:8089/api/deptEmps/${editDeptEmp.emp_id}`, editDeptEmp)
      .then(response => {
        console.log('Updated Department Employee:', response.data);
        // Fetch updated list of customers
        fetchDeptEmps();
        // Close the edit modal
        setIsEditModalOpen(false);
      })
      .catch(error => console.error(error));
  };

  return (
    <div className="container">
      <h1>Department Employee Records</h1>
      <div className="navbar">
        <a href="/">Home</a>
        <a href="/departments">Departments</a>
        <a href="/deptEmployee">Department Employee</a>
        <a href="/employee">Employee</a>
        <a href="/invoice">Invoice</a>
        <a href="/product">Product</a>
        <a href="/productionEmp">Production Employee</a>
        <a href="/productionLine">Production Line</a>
        <a href="/productLine">Product Line</a>
        <a href="/productMaterial">Product Material</a>
        <a href="/rawMaterial">Raw_Material</a>
        <a href="/supplySchedule">SupplySchedule</a>
        <a href="/vendor">Vendor</a>
        <a href="/warehouse">WareHouse</a>
        <a href="/warehouseEmp">Warehouse Employee</a>
        {/* Add other links as needed */}
      </div>
      <table>
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>Department ID</th>
            {/* <th>Phone</th>
            <th>Email</th>
            <th>Actions</th> */}
          </tr>
        </thead>
        <tbody>
          {deptEmp && Array.isArray(deptEmp) ? (
            deptEmp.map(customer => (
              <tr key={customer.emp_id}>
                <td>{customer.emp_id}</td>
                <td>{customer.dept_id}</td>
                {/* <td>{customer.customer_phone}</td>
                <td>{customer.customer_email}</td> */}
                <td>
                  <button onClick={() => handleEditDeptEmp(customer)}>
                    Edit
                  </button>
                  <button onClick={() => handleDeleteDeptEmp(customer.emp_id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">Loading...</td>
            </tr>
          )}
        </tbody>
      </table>

      <h2>Add New Department Employee</h2>
      <div>
        <label>Employee ID:</label>
        <input
          type="ID"
          name="emp_id"
          value={newDeptEmp.emp_id}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Department ID:</label>
        <input
          type="text"
          name="dept_id"
          value={newDeptEmp.dept_id}
          onChange={handleInputChange}
        />
      </div>
      {/* <div>
        <label>Phone:</label>
        <input
          type="text"
          name="customer_phone"
          value={newCustomer.customer_phone}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Email:</label>
        <input
          type="text"
          name="customer_email"
          value={newCustomer.customer_email}
          onChange={handleInputChange}
        />
      </div> */}
      <button onClick={handleAddDeptEmp}>Add Department Employee</button>

      {/* Edit Modal */}
      {isEditModalOpen && (
        <div>
          <h2>Edit Department Employee</h2>
          <div>
            <label>Employee ID:</label>
            <input
              type="text"
              name="customer_id"
              value={editDeptEmp.emp_id}
              disabled
            />
          </div>
          <div>
            <label>Department ID:</label>
            <input
              type="text"
              name="customer_name"
              value={editDeptEmp.dept_id}
              onChange={(e) => setEditDeptEmp({ ...editDeptEmp, dept_id: e.target.value })}
            />
          </div>
          {/* <div>
            <label>Phone:</label>
            <input
              type="text"
              name="customer_phone"
              value={editCustomer.customer_phone}
              onChange={(e) => setEditCustomer({ ...editCustomer, customer_phone: e.target.value })}
            />
          </div>
          <div>
            <label>Email:</label>
            <input
              type="text"
              name="customer_email"
              value={editCustomer.customer_email}
              onChange={(e) => setEditCustomer({ ...editCustomer, customer_email: e.target.value })}
            />
          </div> */}
          <button onClick={handleUpdateDeptEmp}>Update Department Employee</button>
          <button onClick={() => setIsEditModalOpen(false)}>Cancel</button>
        </div>
      )}
    <br></br>
    <br></br>
    </div>
  );
}

export default Customer;
