import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
function Vendor() {
  const [stocks, setStocks] = useState([]);
  const [newStock, setNewStock] = useState({
    // stock_id: '',
    // quantity: '',
    // price: '',
    // product_id: '',
    // retailer_id: '',
    vendor_id: '',
    name: '',
    address: '',
    phone: '',
    speciality: '',
  });
  const [editStock, setEditStock] = useState({
    vendor_id: '',
    name: '',
    address: '',
    phone: '',
    speciality: '',
  });
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  useEffect(() => {
    // Fetch data from the Node.js server
    fetchStocks();
  }, []);

  const fetchStocks = () => {
    axios.get('http://localhost:8089/api/vendors')
      .then(response => {
        console.log('Fetched Vendors:', response.data);
        setStocks(response.data || []);
      })
      .catch(error => console.error(error));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStock(prevStock => ({
      ...prevStock,
      [name]: value,
    }));
  };

  const handleAddStock = () => {
    axios.post('http://localhost:8089/api/vendors', newStock)
      .then(response => {
        console.log('Added Vendor:', response.data);
        fetchStocks();
        setNewStock({
          vendor_id: '',
    name: '',
    address: '',
    phone: '',
    speciality: '',
        });
      })
      .catch(error => console.error(error));
  };

  const handleDeleteStock = (stockId) => {
    axios.delete(`http://localhost:8089/api/vendors/${stockId}`)
      .then(response => {
        console.log('Deleted Vendor:', response.data);
        fetchStocks();
      })
      .catch(error => console.error(error));
  };

  const handleEditStock = (stock) => {
    setEditStock(stock);
    setIsEditModalOpen(true);
  };

  const handleUpdateStock = () => {
    axios.put(`http://localhost:8089/api/vendors/${editStock.vendor_id}`, editStock)
      .then(response => {
        console.log('Updated Vendor:', response.data);
        fetchStocks();
        setIsEditModalOpen(false);
      })
      .catch(error => console.error(error));
  };

  return (
    <div className="container">
      <h1>Vendor Records</h1>
      <div className="navbar">
        <a href="/">Home</a>
        <a href="/departments">Departments</a>
        <a href="/deptEmployee">Department Employee</a>
        <a href="/employee">Employee</a>
        <a href="/invoice">Invoice</a>
        <a href="/product">Product</a>
        <a href="/productionEmp">Production Employee</a>
        <a href="/productionLine">Production Line</a>
        <a href="/productLine">Product Line</a>
        <a href="/productMaterial">Product Material</a>
        <a href="/rawMaterial">Raw_Material</a>
        <a href="/supplySchedule">SupplySchedule</a>
        <a href="/vendor">Vendor</a>
        <a href="/warehouse">WareHouse</a>
        <a href="/warehouseEmp">Warehouse Employee</a>
        {/* Add other links as needed */}
      </div>
      <table>
        <thead>
          <tr>
            <th>Vendor ID</th>
            <th>Vendor Name</th>
            <th>Address</th>
            <th>Phone Number</th>
            <th>Speciality</th>
          </tr>
        </thead>
        <tbody>
          {stocks && Array.isArray(stocks) ? (
            stocks.map(stock => (
              <tr key={stock.vendor_id}>
                <td>{stock.vendor_id}</td>
                <td>{stock.name}</td>
                <td>{stock.address}</td>
                <td>{stock.phone}</td>
                <td>{stock.speciality}</td>
                <td>
                  <button onClick={() => handleEditStock(stock)}>
                    Edit
                  </button>
                  <button onClick={() => handleDeleteStock(stock.vendor_id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">Loading...</td>
            </tr>
          )}
        </tbody>
      </table>

      <h2>Add New Vendor</h2>
      <div>
        <label>Vendor ID:</label>
        <input
          type="text"
          name="stock_id"
          value={newStock.vendor_id}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Name:</label>
        <input
          type="text"
          name="quantity"
          value={newStock.name}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Address:</label>
        <input
          type="text"
          name="price"
          value={newStock.address}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Phone:</label>
        <input
          type="text"
          name="product_id"
          value={newStock.phone}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Speciality:</label>
        <input
          type="text"
          name="retailer_id"
          value={newStock.speciality}
          onChange={handleInputChange}
        />
      </div>
      <button onClick={handleAddStock}>Add Vendor</button>

      {/* Edit Modal */}
      {isEditModalOpen && (
        <div>
          <h2>Edit Vendor</h2>
          <div>
            <label>Vendor ID:</label>
            <input
              type="text"
              name="stock_id"
              value={editStock.vendor_id}
              disabled
            />
          </div>
          <div>
            <label>Name:</label>
            <input
              type="text"
              name="quantity"
              value={editStock.name}
              onChange={(e) => setEditStock({ ...editStock, name: e.target.value })}
            />
          </div>
          <div>
            <label>Address:</label>
            <input
              type="text"
              name="price"
              value={editStock.address}
              onChange={(e) => setEditStock({ ...editStock, address: e.target.value })}
            />
          </div>
          <div>
            <label>Phone Number:</label>
            <input
              type="text"
              name="product_id"
              value={editStock.phone}
              onChange={(e) => setEditStock({ ...editStock, phone: e.target.value })}
            />
          </div>
          <div>
            <label>Speciality :</label>
            <input
              type="text"
              name="retailer_id"
              value={editStock.speciality}
              onChange={(e) => setEditStock({ ...editStock, speciality: e.target.value })}
            />
          </div>
          <button onClick={handleUpdateStock}>Update Vendor</button>
          <button onClick={() => setIsEditModalOpen(false)}>Cancel</button>
        </div>
      )}
      <br></br>
    <br></br>
    </div>
  );
}

export default Vendor;
