import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Inquiry() {
  const [emps, setEmps] = useState([]);
  const [newEmp, setNewEmp] = useState({
    // inquiry_id: '',
    // inquiry_reason: '',
    // inquiry_date: '',
    // inquiry_status: '',
    // customer_id: '',
    emp_id: '',
    first_name: '',
    last_name: '',
    position: '',
    salary: '',
  });
  const [editInquiry, setEditInquiry] = useState({
    // inquiry_id: '',
    // inquiry_reason: '',
    // inquiry_date: '',
    // inquiry_status: '',
    // customer_id: '',
    emp_id: '',
    first_name: '',
    last_name: '',
    position: '',
    salary: '',
  });
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  useEffect(() => {
    // Fetch data from the Node.js server
    fetchEmps();
  }, []);

  const fetchEmps = () => {
    axios.get('http://localhost:8089/api/inquiries')
      .then(response => {
        console.log('Fetched Inquiries:', response.data);
        setEmps(response.data || []);
      })
      .catch(error => console.error(error));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewEmp(prevInquiry => ({
      ...prevInquiry,
      [name]: value,
    }));
  };

  const handleAddInquiry = () => {
    axios.post('http://localhost:8089/api/inquiries', newEmp)
      .then(response => {
        console.log('Added Inquiry:', response.data);
        fetchEmps();
        setNewEmp({
          emp_id: '',
    first_name: '',
    last_name: '',
    position: '',
    salary: '',
        });
      })
      .catch(error => console.error(error));
  };

  const handleDeleteInquiry = (inquiryId) => {
    axios.delete(`http://localhost:8089/api/inquiries/${inquiryId}`)
      .then(response => {
        console.log('Deleted Inquiry:', response.data);
        fetchEmps();
      })
      .catch(error => console.error(error));
  };

  const handleEditInquiry = (inquiry) => {
    setEditInquiry(inquiry);
    setIsEditModalOpen(true);
  };

  const handleUpdateInquiry = () => {
    axios.put(`http://localhost:8089/api/inquiries/${editInquiry.inquiry_id}`, editInquiry)
      .then(response => {
        console.log('Updated Inquiry:', response.data);
        fetchEmps();
        setIsEditModalOpen(false);
      })
      .catch(error => console.error(error));
  };

  return (
    <div className="container">
      <h1>Employee Records</h1>
      <div className="navbar">
        <a href="/">Home</a>
        <a href="/departments">Departments</a>
        <a href="/deptEmployee">Department Employee</a>
        <a href="/employee">Employee</a>
        <a href="/invoice">Invoice</a>
        <a href="/product">Product</a>
        <a href="/productionEmp">Production Employee</a>
        <a href="/productionLine">Production Line</a>
        <a href="/productLine">Product Line</a>
        <a href="/productMaterial">Product Material</a>
        <a href="/rawMaterial">Raw_Material</a>
        <a href="/supplySchedule">SupplySchedule</a>
        <a href="/vendor">Vendor</a>
        <a href="/warehouse">WareHouse</a>
        <a href="/warehouseEmp">Warehouse Employee</a>
        {/* Add other links as needed */}
      </div>
      <table>
        <thead>
          <tr>
            <th>Employee ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Position</th>
            <th>Salary</th>
          </tr>
        </thead>
        <tbody>
          {emps && Array.isArray(emps) ? (
            emps.map(inquiry => (
              <tr key={inquiry.emp_id}>
                <td>{inquiry.emp_id}</td>
                <td>{inquiry.first_name}</td>
                <td>{inquiry.last_name}</td>
                <td>{inquiry.position}</td>
                <td>{inquiry.salary}</td>
                <td>
                  <button onClick={() => handleEditInquiry(inquiry)}>
                    Edit
                  </button>
                  <button onClick={() => handleDeleteInquiry(inquiry.emp_id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">Loading...</td>
            </tr>
          )}
        </tbody>
      </table>

      <h2>Add New Employee</h2>
      <div>
        <label>ID:</label>
        <input
          type="text"
          name="emp_id"
          value={newEmp.emp_id}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>First Name:</label>
        <input
          type="text"
          name="first_name"
          value={newEmp.first_name}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Last Name:</label>
        <input
          type="text"
          name="last_name"
          value={newEmp.last_name}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Position:</label>
        <input
          type="text"
          name="position"
          value={newEmp.position}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Salary:</label>
        <input
          type="text"
          name="salary"
          value={newEmp.salary}
          onChange={handleInputChange}
        />
      </div>
      <button onClick={handleAddInquiry}>Add Employee</button>

      {/* Edit Modal */}
      {isEditModalOpen && (
        <div>
          <h2>Edit Employee</h2>
          <div>
            <label>ID:</label>
            <input
              type="text"
              name="emp_id"
              value={editInquiry.inquiry_id}
              disabled
            />
          </div>
          <div>
            <label>First Name:</label>
            <input
              type="text"
              name="first_name"
              value={editInquiry.first_name}
              onChange={(e) => setEditInquiry({ ...editInquiry, first_name: e.target.value })}
            />
          </div>
          <div>
            <label>Last Name:</label>
            <input
              type="text"
              name="last_name"
              value={editInquiry.last_name}
              onChange={(e) => setEditInquiry({ ...editInquiry, last_name: e.target.value })}
            />
          </div>
          <div>
            <label>Position:</label>
            <input
              type="text"
              name="position"
              value={editInquiry.position}
              onChange={(e) => setEditInquiry({ ...editInquiry, position: e.target.value })}
            />
          </div>
          <div>
            <label>Salary:</label>
            <input
              type="text"
              name="salary"
              value={editInquiry.salary}
              onChange={(e) => setEditInquiry({ ...editInquiry, salary: e.target.value })}
            />
          </div>
          <button onClick={handleUpdateInquiry}>Update Employee</button>
          <button onClick={() => setIsEditModalOpen(false)}>Cancel</button>
        </div>
      )}

<br></br>
    <br></br>
    </div>
  );
}

export default Inquiry;
